import React, { useState } from 'react'


const App = () => {
    const state = useState();
// console.log(state);

const [count, setCount] = useState(50);

const IncNum = () => {
    setCount(100);
    console.log("clicked " + count++);
};

    return (
        <>
        <h1> { count } </h1>
        <button onClick = {IncNum}>Click Me</button>
        </>
    )

}

export default App;
