import React, { useState } from 'react'

const App = () => {
    const purple = '#8344ad';
    const [bg, setBg] = useState(purple);
    const [name, setName] = useState('Click Me');
    const bgChange = () => {
        // console.log("clicked");
        let newBg = "#34495e";
        setBg(newBg);
        setName('ouch ðŸº');
    }
    const bgBack = () => {
        setBg(purple);
        setName('Again? ðŸ˜ ')
    }
    return (
        <>
        <div style = { { backgroundColor : bg } } >
            <button onClick = {bgChange} onDoubleClick = {bgBack}>{name}</button>
        </div>
        </>
    )
}

export default App;
